import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AccountCircleOutlinedIcon from '@material-ui/icons/AccountCircleOutlined';
import PublishIcon from '@material-ui/icons/Publish';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import DoubleArrowIcon from '@material-ui/icons/DoubleArrow';

const VR = () => {
  return (
    <Container>
        <div className="vr-shape">
            <div className="screen">
                <img src="https://eu-images.contentstack.com/v3/assets/blt6b0f74e5591baa03/blt4138e585008d15dd/631967d86519941c4d578e63/sanjose.jpg?width=850&auto=webp&quality=95&format=jpg&disable=upscale" alt="" />
            </div>
            <div className="vr-btns">
                <div className="btn"></div>
                <div className="btn"></div>
                <div className="btn-mid">
                    <DoubleArrowIcon style={{"fontSize" : "2.5rem", "fill" : "black"}}/>
                </div>
                <div className="btn"></div>
                <div className="btn"></div>
            </div>
        </div>
    </Container>
  )
}

export default VR

const Container = styled.div`
    height: 100vh;
    width: 100vw;
    background-color: #171616;
    display: grid;
    place-items: center;



    .vr-shape{
        height: 500px;
        width: 1000px;
        position: relative;
        /* background-color: black; */
        border-radius: 30px;
        border: 1px solid #ccc1c1;
        background-color: #272424;

        display: grid;
        place-items: center;

        padding: 10px;


        .screen{
            height: 450px;
            width: 950px;
            background-color: orange;
            border-radius: 20px;

            overflow-y: hidden;
            overflow-x: scroll;

            img{
                height: 100%;
                width: 150%;
                /* margin-left: -50%; */
            }
        }

        .screen::-webkit-scrollbar {
            display: none;
        }

        .vr-btns{
            position: absolute;
            bottom: 35px;
            left: 75px;
            height: 50px;
            width: 850px;
            background: white;
            border-radius: 20px;

            display: flex;
            justify-content: space-between;
            align-items: center;

            box-shadow: rgb(40 35 35) 0px 0px 50px 0px;
            backdrop-filter: blur(8px);

            .btn{

            }

            .btn-mid{
                height: 90px;
                width: 90px;
                border-radius: 100%;
                background-color: #fff;
                border: 1px solid #f8e2e2;
                box-shadow: rgb(150 142 142) 0px 0px 15px 0px;

                display: grid;
                place-items: center;
            }
        }
    }


`
