import React, { useState, useEffect } from 'react'
import styled from 'styled-components'

const Landing = () => {
  return (
    <Container>
        <SlideOne>
          <div className="nav"></div>
          <div className="main">
            <div className="text">
              <div className='product-name'>Vision Bridge</div>
              <h2>Helping to break down barriers for the visually impaired and create new opportunities for independence.</h2>
            </div>
            <div className="image">
              <img src="https://im.indiatimes.in/media/content/2018/Feb/_1518700069.jpg" alt="" />
            </div>
          </div>
        </SlideOne>
    </Container>
  )
}

export default Landing

const Container = styled.div`

`

const SlideOne = styled.div`
  width: 100vw;
  height: 100vh;
  /* background: #0B2447; */


  .nav{
    height: 70px;
    border-bottom: 1px solid black;
  }

  .main{
    height: calc(100vh - 70px);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 30px;

    .text{
      margin-right: 30px;
      margin-left: 100px;
      display: flex;
      flex-direction: column;
      align-items: flex-start;

      .product-name{
        font-size: 1rem;
        background-color: #333;
        color: white;
        padding: 10px 20px;
        letter-spacing: 0.1rem;
        margin-bottom: 10px;
      }

      h2{
        font-weight: 400;
        font-size: 1.75rem;
        letter-spacing: 0.1rem;
      }


    }

    .image{
      img{
        width: calc(45vw - 30px);
      }
    }
  }
`
